package com.toy.fragment.ui.main;


import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.Toast;

import com.bumptech.glide.Glide;
import com.toy.fragment.MainActivity;
import com.toy.fragment.R;

public class MainFragment extends Fragment {

    public static MainFragment newInstance() {
        return new MainFragment();
    }

    private View mainFragmentView;
    private Button startClock;
    private static String TAG = "MainFragment";
    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {
        mainFragmentView = inflater.inflate(R.layout.main_fragment, container, false);
        ImageView imageView =(ImageView) mainFragmentView.findViewById(R.id.mainImageView);
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .into(imageView);

        startClock = mainFragmentView.findViewById(R.id.startclock);
        startClock.setOnClickListener( new View.OnClickListener(){

            @Override
            public void onClick(View view) {

                Log.d(TAG, "start Clock Fragment");
                Toast.makeText(mainFragmentView.getContext(), "test :" +getActivity().getLocalClassName(), Toast.LENGTH_LONG).show();
                ((MainActivity) getActivity()).replaceFragment(MyClockFragment.newInstance());

            }
        });

        return mainFragmentView;
    }

}